function multiple( str, count ) {
     if ( typeof count == "undefined" ) {
         count = 2;
     }
     var i;
     var copies = "";
     for (i = 0; i < count; i++) {
         copies += str;
     }
     return copies;
}

function sum() {
    var i;
    var total = 0;
    for (i = 0; i < arguments.length; i++) {
         total += arguments[i];
    }
    return total;
}

